<?php\n$biggest = 0;\n$counter = 1;\n$numberWithBiggestSteps = 0;\n\nfunction test($i, $original) {\n    global $biggest;\n    global $counter;\n    global $numberWithBiggestSteps;\n\n    if ($i == 1) {\n        if ($counter > $biggest) {\n            $biggest = $counter;\n            $numberWithBiggestSteps = $original;\n        }\n        // Reset counter for the next number\n        $counter = 1;\n        return;\n    } else {\n        $counter++;\n        if ($i % 2 == 0) {\n            test($i / 2, $original);\n        } else {\n            test(3 * $i + 1, $original);\n        }\n    }\n}\n\nfor ($i = 1; $i < 1000000; $i++) {\n    test($i, $i);\n}\n\necho "The starting number that yields the most steps is $numberWithBiggestSteps with $biggest steps.";\n?>