<?php\n// Path to the MS Access database file\n$db_path = "path_to_your_database.mdb";\n\n// Create a new COM object for ADODB.Connection\n$odb_conn = new COM("ADODB.Connection");\n\n// Connection string for MS Access database\n$connstr = "DRIVER={Microsoft Access Driver (*.mdb)}; DBQ=". $db_path.";";\n\n// Open the connection\n$odb_conn->open($connstr);\n\n// Retrieve schema information for tables\n$rs_meta = $odb_conn->OpenSchema(20, array(Null, Null, Null, "TABLE"));\n\n// Loop through the schema information and display it\nwhile (!$rs_meta->EOF) {\n    echo "Table Name: " . $rs_meta->Fields["TABLE_NAME"]->value . "<br>";\n    $rs_meta->MoveNext();\n}\n\n// Close the recordset and connection\n$rs_meta->Close();\n$odb_conn->Close();\n?>