<?php\n// Independent variables\n$dbHost = "localhost";\n$dbPort = 5432;\n$dbName = "masi";\n$dbUser = "masi";\n$dbPassword = "123456";\n\n// Create connection string\n$connString = "host=$dbHost port=$dbPort dbname=$dbName user=$dbUser password=$dbPassword";\n\n// Establish connection\n$dbconn = pg_connect($connString);\n\n// Check if connection is successful\nif (!$dbconn) {\n    echo "An error occurred while connecting to the database.\n";\n    exit;\n}\n\n// Prepare SQL query with placeholders to prevent SQL injection\n$sql = "SELECT username, passhash_md5, email \n        FROM users \n        WHERE username=$1 AND email=$2 AND passhash_md5=$3";\n\n// Input values\n$username = 'a';\n$email = 'a';\n$passhash_md5 = 'a';\n\n// Execute the query\n$result = pg_query_params($dbconn, $sql, array($username, $email, $passhash_md5));\n\n// Check if query execution is successful\nif (!$result) {\n    echo "An error occurred while executing the query.\n";\n    exit;\n}\n\n// Process the result\nwhile ($row = pg_fetch_assoc($result)) {\n    echo "Username: " . htmlspecialchars($row['username']) . "\n";\n    echo "Email: " . htmlspecialchars($row['email']) . "\n";\n    // Do not display the password hash for security reasons\n}\n\n// Free result\npg_free_result($result);\n\n// Close the connection\npg_close($dbconn);\n?>