function foo($param_1) {\n    static $initialized = false;\n\n    if (!$initialized) {\n        doExpensiveStuff($param_1);\n        $initialized = true;\n    }\n\n    $foo = function($param_1) {\n        echo '<b>' . $param_1 . '</b>';\n    };\n\n    $foo($param_1);\n}\n\nfunction doExpensiveStuff($param) {\n    // Simulate an expensive operation\n    sleep(2);\n    echo "Expensive stuff done with $param\n";\n}\n\n// Example usage\nfoo('First call'); // This will perform the expensive operation\nfoo('Second call'); // This will skip the expensive operatio