function appendDepth(&$array, $currentDepth = 0) {\n    foreach ($array as &$item) {\n        if (is_array($item)) {\n            if (!isset($item['depth'])) {\n                $item['depth'] = $currentDepth;\n            }\n            if (isset($item['children']) && is_array($item['children'])) {\n                appendDepth($item['children'], $currentDepth + 1);\n            }\n        }\n    }\n}\n\n// Example array\n$array = [\n    'title' => 'Website Navigation',\n    'path' => '',\n    'type' => '115',\n    'pid' => 0,\n    'hasChildren' => 1,\n    'children' => [\n        53 => [\n            'title' => 'Home',\n            'path' => '',\n            'type' => '118',\n            'pid' => 52,\n            'hasChildren' => 0,\n        ],\n        54 => [\n            'title' => 'Features',\n            'path' => 'features',\n            'type' => '374',\n            'pid' => 52,\n            'hasChildren' => 1,\n            'children' => [\n                59 => [\n                    'title' => 'artistic',\n                    'path' => 'features/artistic',\n                    'type' => '374',\n                    'pid' => 54,\n                    'hasChildren' => 1,\n                    'children' => [\n                        63 => [\n                            'title' => 'galleries',\n                            'path' => 'features/artistic/galleries',\n                            'type' => '374',\n                            'pid' => 59,\n                            'hasChildren' => 1,\n                            'children' => [\n                                65 => [\n                                    'title' => 'graphics',\n                                    'path' => 'features/artistic/galleries/graphics',\n                                    'type' => '118',\n                                    'pid' => 63,\n                                    'hasChildren' => 0,\n                                ],\n                                67 => [\n                                    'title' => 'mixed medium',\n                                    'path' => 'features/artistic/galleries/mixed-medium',\n                                    'type' => '118',\n                                    'pid' => 63,\n                                    'hasChildren' => 0,\n                                ],\n                                64 => [\n                                    'title' => 'overview',\n                                    'path' => 'features/artistic/galleries',\n                                    'type' => '374',\n                                    'pid' => 59,\n                                    'hasChildren' => 0,\n                                ],\n                            ],\n                        ],\n                    ],\n                ],\n            ],\n        ],\n    ],\n];\n\nappendDepth($array);\n\nprint_r($array);