$global_value = 10; // Example global value\n\n    $sql = "SELECT player_name, base_name, ab, df, ce, ((ab + df + ce) / $global_value) AS calc_result FROM your_table ORDER BY calc_result DESC";\n    $result = $conn->query($sql);\n\n    if ($result->num_rows > 0) {\n        while($row = $result->fetch_assoc()) {\n            echo "Player: " . $row["player_name"]. " - Base: " . $row["base_name"]. " - Calculation: " . $row["calc_result"]. "<br>";\n        }\n    } else {\n        echo "0 results";\n    }