function CatRemove($myXML, $categoryToRemoveId) {\n    $xmlDoc = new DOMDocument();\n    $xmlDoc->load($myXML);\n    \n    // Get all category elements\n    $categories = $xmlDoc->getElementsByTagName("category");\n    \n    foreach ($categories as $category) {\n        // Check if this category has the id we want to remove\n        if ($category->getAttribute('id') == $categoryToRemoveId) {\n            $category->parentNode->removeChild($category);\n            break; // Exit the loop once the category is found and removed\n        }\n    }\n    \n    // Save the updated XML back to the file\n    $xmlDoc->save($myXML);\n}\n\n// Example usage:\nif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['categorytoremoveValue'])) {\n    CatRemove('path/to/your/file.xml', $_POST['categorytoremoveValue']);\n}