<?php\n// Define the parent URL that should be the referrer\n$parentUrl = 'http://foobar/path/parentpage.aspx';\n\n// Check if HTTP_REFERER is set and matches the parent URL\nif (!isset($_SERVER['HTTP_REFERER']) || strpos($_SERVER['HTTP_REFERER'], $parentUrl) !== 0) {\n    // Redirect to the login page if not loaded within the iframe\n    header('Location: http://foobar/path/login.aspx');\n    exit;\n}\n\n// Your wiki content goes here\n?>