$sourcePort = 21000; // The source port you want to use\n$destination = 'remote_host'; // The remote host\n$port = 12345; // The destination port\n$message = 'Your message';\n\n// Create a UDP socket\n$socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);\nif ($socket === false) {\n    echo "Socket creation failed: " . socket_strerror(socket_last_error()) . "\n";\n    exit;\n}\n\n// Bind the socket to the source port\nif (socket_bind($socket, '0.0.0.0', $sourcePort) === false) {\n    echo "Socket bind failed: " . socket_strerror(socket_last_error($socket)) . "\n";\n    socket_close($socket);\n    exit;\n}\n\n// Send the message to the destination\nif (socket_sendto($socket, $message, strlen($message), 0, $destination, $port) === false) {\n    echo "Socket sendto failed: " . socket_strerror(socket_last_error($socket)) . "\n";\n    socket_close($socket);\n    exit;\n}\n\n// Close the socket\nsocket_close($socket);\necho "Message sent successfully from port $sourcePort\n";