$_error = 4; // Example error code\n$error_msg = [\n    0 => 'No Error',\n    1 => 'Error 1',\n    2 => 'Error 2',\n    3 => 'No numbers allowed',\n    // other error messages...\n];\n\nif ($_error == 0) {\n    // No error\n    $no_errors = TRUE;\n} else {\n    $no_errors = FALSE;\n}\n\nif ($_error == 3) {\n    $error_3 = TRUE;\n} else {\n    $error_3 = FALSE;\n}\n\nif (!$no_errors) {\n    if ($error_3) {\n        // Handle error 3 specifically\n        echo 'Error 3: No numbers allowed';\n        // Bunch of stuff happens;\n    } else {\n        // Handle other errors\n        echo 'Error ' . $_error . ': ' . $error_msg[$_error];\n        // Bunch of other stuff;\n    }\n}