function path_is_valid($path) {\n    // Base directory\n    $baseDir = realpath(__DIR__ . '/sample');\n\n    // Resolve the absolute path\n    $resolvedPath = realpath($baseDir . '/' . $path);\n\n    // Check if the resolved path is within the base directory\n    if ($resolvedPath && strpos($resolvedPath, $baseDir) === 0) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n$path = $_GET['path'];\nif(path_is_valid($path) && is_file("sample/$path")) {\n    header('Content-Type: text/plain');\n    readfile("sample/$path");\n} else {\n    // Handle invalid paths appropriately\n    http_response_code(400);\n    echo "Invalid path";\n}