use PHPUnit\Framework\TestCase;\n\nclass CreateFileLinkTest extends TestCase\n{\n    public function testCreateFileLink()\n    {\n        // Mock the date function to return a fixed value\n        $mockDateFunction = function() {\n            return '01-01-22-12-00';\n        };\n\n        // Call the function with the mocked date function\n        $result = CreateFileLink($mockDateFunction);\n        \n        // Assert the expected result\n        $this->assertEquals('01-01-22-12-00', $result);\n    }\n}