<?php\n// Ensure the file is being accessed through the application\nif (!defined('IS_ADMIN_FLAG')) {\n    die('Illegal Access');\n}\n\n// Configuration\n$random_products_limit = 5; // Number of random products to display\n$random_category_id = 1; // Category ID to fetch products from\n\n// Fetch random products from the specified category\n$random_products_query = tep_db_query("\n    SELECT p.products_id, p.products_image, pd.products_name\n    FROM " . TABLE_PRODUCTS . " p\n    JOIN " . TABLE_PRODUCTS_DESCRIPTION . " pd ON p.products_id = pd.products_id\n    JOIN " . TABLE_PRODUCTS_TO_CATEGORIES . " ptc ON p.products_id = ptc.products_id\n    WHERE p.products_status = '1' \n    AND pd.language_id = '" . (int)$languages_id . "'\n    AND ptc.categories_id = '" . (int)$random_category_id . "'\n    ORDER BY RAND()\n    LIMIT " . (int)$random_products_limit\n);\n\nif (tep_db_num_rows($random_products_query) > 0) {\n    echo '<div class="random-products">';\n    echo '<h2>Random Products</h2>';\n    echo '<ul>';\n    while ($random_product = tep_db_fetch_array($random_products_query)) {\n        echo '<li>';\n        echo '<a href="' . tep_href_link(FILENAME_PRODUCT_INFO, 'products_id=' . $random_product['products_id']) . '">';\n        echo tep_image(DIR_WS_IMAGES . $random_product['products_image'], $random_product['products_name']);\n        echo '<br>' . $random_product['products_name'];\n        echo '</a>';\n        echo '</li>';\n    }\n    echo '</ul>';\n    echo '</div>';\n}\n?>