<?php\n// URL for the WebDAV request\n$webdav_url = 'https://your-exchange-server/exchange/your-email@domain.com/Inbox/';\n\n// Initialize cURL session\n$ch = curl_init($webdav_url);\n\n// Configure cURL options\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookies.txt'); // Use cookies from the previous authentication\ncurl_setopt($ch, CURLOPT_HTTPHEADER, [\n    'Content-Type: text/xml', // If needed\n    'Depth: 1' // Adjust based on your needs\n]);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // Ignore SSL certificate validation (only for testing purposes)\n\n// Execute the WebDAV request\n$response = curl_exec($ch);\n\nif (curl_errno($ch)) {\n    echo 'Error: ' . curl_error($ch);\n} else {\n    echo 'WebDAV request successful';\n    echo $response; // Output the response from the server\n}\n\ncurl_close($ch);\n?>