<?php\nfunction benchmark($function, $iterations = 10000) {\n    // Warm-up\n    for ($i = 0; $i < 100; $i++) {\n        $function();\n    }\n    \n    // Measure\n    $start = microtime(true);\n    for ($i = 0; $i < $iterations; $i++) {\n        $function();\n    }\n    $end = microtime(true);\n    \n    return $end - $start;\n}\n\n$w = 'world';\n\n// Define the test functions\n$tests = [\n    'Concatenation' => function() use ($w) {\n        echo 'Hello ' . $w . '!';\n    },\n    'Inline Var' => function() use ($w) {\n        echo "Hello $w!";\n    },\n    'Addition Operator' => function() use ($w) {\n        echo 'Hello ' + $w + '!';\n    }\n];\n\n$results = [];\nforeach ($tests as $name => $test) {\n    // Run multiple times and average\n    $total_time = 0;\n    $runs = 10;\n    for ($i = 0; $i < $runs; $i++) {\n        $total_time += benchmark($test);\n    }\n    $average_time = $total_time / $runs;\n    $results[$name] = $average_time;\n}\n\n// Output results\nforeach ($results as $name => $time) {\n    echo "$name: $time seconds\n";\n}\n?>