use Respect\Validation\Validator as v;\n\n   $nameValidator = v::stringType()->notEmpty()->length(1, 255);\n   $emailValidator = v::email()->notEmpty();\n   $passwordValidator = v::stringType()->notEmpty()->length(8, null);\n\n   $nameValidator->validate($name); // returns true or false\n   $emailValidator->validate($email); // returns true or false\n   $passwordValidator->validate($password); // returns true or false