<?php\n// Initialize the DOMDocument\n$doc = new DOMDocument();\n\n// Load your HTML content\n$html = '<div class="info"><h5>title</h5>text <a href="somelink">anchor</a></div>';\n@$doc->loadHTML($html);\n\n// Initialize DOMXPath\n$xpath = new DOMXPath($doc);\n\n// Query the desired nodes\n$nodes = $xpath->query('//div[@class="info"]');\n\nforeach ($nodes as $node) {\n    // Import the node into a new document to preserve formatting\n    $newDoc = new DOMDocument();\n    $importedNode = $newDoc->importNode($node, true);\n    $newDoc->appendChild($importedNode);\n    \n    // Get the HTML content of the node\n    $formattedContent = $newDoc->saveHTML();\n    echo $formattedContent;\n}\n?>