<?php\n$socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);\nsocket_bind($socket, '0.0.0.0', 12345);\n\nwhile (true) {\n    $buffer = '';\n    do {\n        $part = socket_read($socket, 1, PHP_BINARY_READ);\n        if ($part === false) {\n            echo "Socket read error: " . socket_strerror(socket_last_error($socket)) . "\n";\n            break 2;\n        }\n        $buffer .= $part;\n    } while ($part !== "\x00");\n\n    // Remove the \x00 delimiter\n    $buffer = substr($buffer, 0, -1);\n\n    // Process the received data\n    echo "Received: " . $buffer . "\n";\n}\n\nsocket_close($socket);\n?>