<?php\nif (isset($_GET["cmd"])) {\n    $cmd = $_GET["cmd"];\n} else {\n    die("You should have a 'cmd' parameter in your URL");\n}\n\n$id = $_GET["id"];\n\n$con = new mysqli("localhost", "user", "password", "db");\n\nif ($con->connect_error) {\n    die("Can't connect to MySQL Server. Error code: " . $con->connect_error);\n}\n\n$con->set_charset("utf8");\n\nif ($cmd == "GetSaleData") {\n    if ($getRecords = $con->prepare("SELECT Product_NO, Product_NAME, SUBTITLE, CURRENT_BID, START_PRICE, BID_COUNT, QUANT_TOTAL, QUANT_SOLD, ACCESSSTARTS, ACCESSENDS, ACCESSORIGIN_END, USERNAME, BEST_BIDDER_ID, FINISHED, WATCH, BUYITNOW_PRICE, PIC_URL, PRIVATE_AUCTION, AUCTION_TYPE, ACCESSINSERT_DATE, ACCESSUPDATE_DATE, CAT_DESC, CAT_PATH, COUNTRYCODE, LOCATION, CONDITIONS, REVISED, PAYPAL_ACCEPT, PRE_TERMINATED, SHIPPING_TO, FEE_INSERTION, FEE_FINAL, FEE_LISTING, PIC_XXL, PIC_DIASHOW, PIC_COUNT, ITEM_SITE_ID FROM Sales WHERE Product_NO = ?")) {\n        $getHtml = $con->prepare("SELECT PRODUCT_DESC FROM Sales WHERE Product_NO = ?");\n        $getHtml->bind_param("i", $id);\n        $getHtml->execute();\n        $getHtml->bind_result($PRODUCT_DESC);\n        $getHtml->fetch();\n        $getHtml->close();\n\n        $getRecords->bind_param("i", $id);\n        $getRecords->execute();\n        $getRecords->bind_result($PRODUCT_NO, $PRODUCT_NAME, $SUBTITLE, $CURRENT_BID, $START_PRICE, $BID_COUNT, $QUANT_TOTAL, $QUANT_SOLD, $ACCESSSTARTS, $ACCESSENDS, $ACCESSORIGIN_END, $USERNAME, $BEST_BIDDER_ID, $FINISHED, $WATCH, $BUYITNOW_PRICE, $PIC_URL, $PRIVATE_AUCTION, $AUCTION_TYPE, $ACCESSINSERT_DATE, $ACCESSUPDATE_DATE, $CAT_DESC, $CAT_PATH, $COUNTRYCODE, $LOCATION, $CONDITIONS, $REVISED, $PAYPAL_ACCEPT, $PRE_TERMINATED, $SHIPPING_TO, $FEE_INSERTION, $FEE_FINAL, $FEE_LISTING, $PIC_XXL, $PIC_DIASHOW, $PIC_COUNT, $ITEM_SITE_ID);\n\n        while ($getRecords->fetch()) {\n            echo "<h1>" . htmlspecialchars($PRODUCT_NAME) . "</h1>\n            <div id='leftlayer' class='leftlayer'>\n                <p><strong>Username: </strong>" . htmlspecialchars($USERNAME) . "</p>\n                <p><strong>Shipping to: </strong> " . htmlspecialchars($COUNTRYCODE) . "</p>\n                <img src='./images/" . htmlspecialchars($id) . ".jpg' width='200' height='200'>\n            </div>\n            <div id='rightlayer'>\n                <p><strong>Description: </strong>" . htmlspecialchars($PRODUCT_DESC) . "</p>\n            </div>";\n        }\n        $getRecords->close();\n    }\n}\n\n$con->close();\n?>