<?php\n// Assuming $dateFromDatabase contains the date in 'Y-m-d' format from the database\n$dateFromDatabase = '2023-07-01';\n\n// Get the current date and subtract 13 days\n$thresholdDate = date('Y-m-d', strtotime('-13 days'));\n\n// Compare the dates\nif ($dateFromDatabase < $thresholdDate) {\n    // The date is more than 13 days ago\n    echo "The date is more than 13 days ago.";\n} else {\n    // The date is within the last 13 days\n    echo "The date is within the last 13 days.";\n}\n?>