<?php\nclass Home extends MY_Controller {\n\n    public function index() {\n        // Load widgets for Home Page\n        $this->data['feature'] = $this->load->view('widgets/home_feature', '', true);\n        $this->data['login'] = $this->load->view('widgets/login', '', true);\n        $this->data['calendar'] = $this->load->view('widgets/calendar_home', '', true);\n        $this->data['photos'] = $this->load->view('widgets/photos_scroll', '', true);\n        $this->data['tags'] = $this->load->view('widgets/tags_view', '', true);\n        $this->data['spotlight'] = $this->load->view('widgets/spotlight', '', true);\n        $this->data['recent'] = $this->load->view('widgets/activityfeed', '', true);\n\n        // Set additional data\n        $this->data['title'] = 'Philly2Night.com';\n        $this->data['MetaDesc'] = 'Cities2Night new social network';\n        $this->data['MetaKeywords'] = 'Social, Party, Plan, Events';\n\n        // Load the main view\n        $this->load->view('pages/home_view', $this->data);\n    }\n}