<?php\nclass About extends MY_Controller {\n\n    public function index() {\n        // Load widgets for About Page\n        $this->data['content'] = $this->load->view('widgets/about_content', '', true);\n\n        // Set additional data\n        $this->data['title'] = 'About Us';\n        $this->data['MetaDesc'] = 'Learn more about us';\n        $this->data['MetaKeywords'] = 'About, Company, Info';\n\n        // Load the main view\n        $this->load->view('pages/about_view', $this->data);\n    }\n}