<?php\n   // Database connection\n   $host = 'your_database_host';\n   $db = 'your_database_name';\n   $user = 'your_database_user';\n   $pass = 'your_database_password';\n   $charset = 'utf8mb4';\n\n   $dsn = "mysql:host=$host;dbname=$db;charset=$charset";\n   $options = [\n       PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n       PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n       PDO::ATTR_EMULATE_PREPARES   => false,\n   ];\n\n   try {\n       $pdo = new PDO($dsn, $user, $pass, $options);\n   } catch (\PDOException $e) {\n       throw new \PDOException($e->getMessage(), (int)$e->getCode());\n   }\n\n   // Get incoming SMS data from Twilio\n   $from = $_POST['From'];\n   $body = $_POST['Body'];\n\n   // Insert into database\n   $stmt = $pdo->prepare("INSERT INTO received_sms (from_number, message_body) VALUES (:from_number, :message_body)");\n   $stmt->execute(['from_number' => $from, 'message_body' => $body]);\n\n   // Respond to Twilio (optional)\n   header('Content-Type: text/xml');\n   echo '<Response></Response>';