<?php\ntry {\n    $pdo = new PDO('mysql:host=localhost;dbname=testdb', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Step 1: Use SQL_CALC_FOUND_ROWS in your SELECT query\n    $stmt = $pdo->prepare('SELECT SQL_CALC_FOUND_ROWS * FROM your_table WHERE your_condition');\n    $stmt->execute();\n\n    // Step 2: Fetch the actual data\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Step 3: Execute a second query to get the number of rows found\n    $rowCountStmt = $pdo->query('SELECT FOUND_ROWS()');\n    $rowCount = $rowCountStmt->fetchColumn();\n\n    echo "Number of rows found: " . $rowCount . "\n";\n    print_r($results);\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>