<?php\n// Start the session with a custom session cookie domain\nsession_set_cookie_params([\n    'lifetime' => 0, // Session cookie will expire when the browser is closed\n    'path' => '/',\n    'domain' => '.example.com', // The dot (.) makes the cookie available to all subdomains\n    'secure' => true, // Ensures the cookie is sent over HTTPS only\n    'httponly' => true, // Prevents JavaScript access to the session cookie\n    'samesite' => 'Strict' // Adjust this based on your requirements (Lax, Strict, None)\n]);\n\nsession_start();\n\n// Your session logic here\n$_SESSION['user'] = 'JohnDoe';\n\n// Check if you need to redirect to another subdomain\nif ($_SERVER['HTTP_HOST'] === 'abc.example.com' && $_SERVER['REQUEST_URI'] !== '/') {\n    header('Location: https://www.example.com' . $_SERVER['REQUEST_URI']);\n    exit();\n}\n?>