<?php\n   $database_env = getenv('DATABASE_ENV');\n\n   if ($database_env === 'live') {\n       $database = 'live_database';\n   } elseif ($database_env === 'testing') {\n       $database = 'testing_database';\n   } else {\n       // Default to live or handle error\n       $database = 'live_database';\n   }\n\n   // Now you can use $database to connect to your database\n   echo "Current database: " . $database;\n   ?>