<?php\n$xmlString = '<time hour="18" minute="05" timezone="Eastern" utc-hour="-4" utc-minute="00" />';\n$xml = simplexml_load_string($xmlString);\n\n// Extract attributes\n$hour = (string) $xml->attributes()->hour;\n$minute = (string) $xml->attributes()->minute;\n$timezone = (string) $xml->attributes()->timezone;\n\n// Create a DateTime object\n$timeString = $hour . ":" . $minute;\n$dateTime = DateTime::createFromFormat('H:i', $timeString, new DateTimeZone('UTC'));\n\n// Adjust timezone if necessary (for example, Eastern Time)\n$timezoneMap = [\n    'Eastern' => 'America/New_York',\n    // Add other mappings if needed\n];\n\nif (array_key_exists($timezone, $timezoneMap)) {\n    $dateTime->setTimezone(new DateTimeZone($timezoneMap[$timezone]));\n}\n\n// Print the time in the desired formats\necho $dateTime->format('H:i') . "<br>"; // 24-hour format\necho $dateTime->format('g:i A') . "<br>"; // 12-hour format with AM/PM\n?>