<?php\n$array = array(\n    1 => '1233',\n    2 => '12334',\n    3 => 'Hello',\n    4 => 'hello',\n    5 => 'U'\n);\n\nfunction getCaseInsensitiveDuplicates($array) {\n    $lowercasedArray = array_map('strtolower', $array);\n    $duplicates = array();\n    $counts = array_count_values($lowercasedArray);\n    \n    foreach ($counts as $key => $count) {\n        if ($count > 1) {\n            foreach ($array as $originalKey => $value) {\n                if (strtolower($value) == $key) {\n                    $duplicates[$originalKey] = $value;\n                }\n            }\n        }\n    }\n    \n    return $duplicates;\n}\n\n$duplicates = getCaseInsensitiveDuplicates($array);\nprint_r($duplicates);\n?>