function get_db_conn() {\n    $conn = mysql_connect($GLOBALS['db_ip'], $GLOBALS['db_user'], $GLOBALS['db_pass']);\n    if (!$conn) {\n        die("Database connection failed: " . mysql_error());\n    }\n    if (!mysql_select_db($GLOBALS['db_name'], $conn)) {\n        die("Database selection failed: " . mysql_error());\n    }\n    return $conn;\n}\n\n$conn = get_db_conn();\n\n$query = "INSERT INTO users (uid, twname, privacy) VALUES (15400743, 'gdhdh', 'accepted')";\n$result = mysql_query($query, $conn);\n\nif (!$result) {\n    die("A MySQL error has occurred.<br />Your Query: " . $query . "<br /> Error: (" . mysql_errno($conn) . ") " . mysql_error($conn));\n} else {\n    echo "You have been entered into our Database!";\n}\n\n// Close the connection\nmysql_close($conn);