$first = 2;\n$second = 5;\n$operation = '*';\n\nswitch ($operation) {\n    case '+':\n        $answer = $first + $second;\n        break;\n    case '-':\n        $answer = $first - $second;\n        break;\n    case '*':\n        $answer = $first * $second;\n        break;\n    case '/':\n        if ($second != 0) {\n            $answer = $first / $second;\n        } else {\n            $answer = 'Division by zero error';\n        }\n        break;\n    default:\n        $answer = 'Invalid operation';\n        break;\n}\n\necho $answer;