<?php\n$servername = "your_server_name";\n$username = "your_username";\n$password = "your_password";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to get all databases with the prefix "Whatever_"\n$sql = "SHOW DATABASES LIKE 'Whatever_%'";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        $dbName = $row['Database'];\n        // Drop each database\n        $dropSql = "DROP DATABASE `$dbName`";\n        if ($conn->query($dropSql) === TRUE) {\n            echo "Database $dbName dropped successfully.\n";\n        } else {\n            echo "Error dropping database $dbName: " . $conn->error . "\n";\n        }\n    }\n} else {\n    echo "No databases found with the specified prefix.\n";\n}\n\n$conn->close();\n?>