function core_oo($function) {\n    static $wrap = array(\n        'comment' => 'the_comment',\n        'comments' => 'have_comments',\n        'post' => 'the_post',\n        'posts' => 'have_posts'\n    );\n    \n    $args = array_slice(func_get_args(), 1);\n\n    if (!array_key_exists($function, $wrap)) {\n        throw new InvalidArgumentException("Function $function does not exist in the wrap array.");\n    }\n\n    if (!function_exists($wrap[$function])) {\n        throw new BadFunctionCallException("The function {$wrap[$function]} is not defined.");\n    }\n\n    return call_user_func_array($wrap[$function], $args);\n}