<?php\n    namespace App\Controller;\n\n    use Cake\Controller\Controller;\n    use Cake\Event\Event;\n    use Cake\Network\Exception\MissingControllerException;\n    use Cake\Network\Exception\MissingActionException;\n    use Cake\Routing\Router;\n\n    class AppErrorController extends AppController\n    {\n        public function initialize()\n        {\n            parent::initialize();\n            // Load any additional components you need here\n        }\n\n        public function beforeFilter(Event $event)\n        {\n            parent::beforeFilter($event);\n            // Add any additional logic you need here\n        }\n\n        public function handleError($error)\n        {\n            // Custom error handling logic\n            // Ensure you log the error or handle it as needed\n        }\n\n        public function error404()\n        {\n            $this->response = $this->response->withStatus(404);\n            $this->set('title', '404 Not Found');\n            $this->render('/Error/error404'); // Ensure you have this template\n        }\n\n        public function error500()\n        {\n            $this->response = $this->response->withStatus(500);\n            $this->set('title', '500 Internal Server Error');\n            $this->render('/Error/error500'); // Ensure you have this template\n        }\n    }