function tokenize($expression) {\n    // Define the delimiters (operators in this case)\n    $delimiters = '/([\+\-\*\/\^\(\)])/';\n    \n    // Use preg_split to split the expression and keep the delimiters\n    $tokens = preg_split($delimiters, $expression, -1, PREG_SPLIT_DELIM_CAPTURE | PREG_SPLIT_NO_EMPTY);\n    \n    return $tokens;\n}\n\n// Example usage:\n$expression = "3 + 5 * (2 - 8)";\n$tokens = tokenize($expression);\n\nprint_r($tokens);