class Database {\n    protected static $pdo;\n\n    public static function setPDO($pdo) {\n        self::$pdo = $pdo;\n    }\n\n    protected static function prepareAndExecute($query, $params) {\n        static $statements = [];\n        if (!isset($statements[$query])) {\n            $statements[$query] = self::$pdo->prepare($query);\n        }\n        $statements[$query]->execute($params);\n        return $statements[$query];\n    }\n}\n\nclass Product extends Database {\n    static function getPrice($id) {\n        $stmt = self::prepareAndExecute('SELECT price FROM products WHERE id = ?', [$id]);\n        return $stmt->fetchColumn(0);\n    }\n}\n\n// Usage\nDatabase::setPDO($pdo);\n$price = Product::getPrice(4982);\necho "Product 4982 costs $price\n";