defined('_JEXEC') or die('Restricted access');\n\njimport('joomla.application.component.view');\n\nclass YourComponentViewYourView extends JViewLegacy\n{\n    public function display($tpl = null)\n    {\n        // Set the MIME type to application/pdf\n        $app = JFactory::getApplication();\n        $document = JFactory::getDocument();\n        $document->setMimeEncoding('application/pdf');\n\n        // Set the filename for the PDF download\n        $app->setHeader('Content-Disposition', 'attachment; filename="your_file_name.pdf"', true);\n\n        // Render the rest of your view\n        parent::display($tpl);\n    }\n}