<?php\nrequire_once('lib/nusoap.php'); // Adjust the path to your nusoap library\n\n$client = new nusoap_client('http://www.example.com/soap?wsdl', 'wsdl');\n$err = $client->getError();\nif ($err) {\n    echo 'Constructor error: ' . $err;\n}\n\n// Call a function in the SOAP service\n$result = $client->call('YourFunction', array('param1' => 'value1', 'param2' => 'value2'));\n\nif ($client->fault) {\n    echo 'Fault: ';\n    print_r($result);\n} else {\n    $err = $client->getError();\n    if ($err) {\n        echo 'Error: ' . $err;\n    } else {\n        echo 'Result: ';\n        print_r($result);\n    }\n}\n?>