// Function to run a script in the background\nfunction runInBackground($command) {\n    if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') {\n        // Windows\n        pclose(popen("start /B " . $command, "r"));\n    } else {\n        // Unix/Linux\n        exec($command . " > /dev/null &");\n    }\n}\n\n// Example usage\n$scriptPath = '/path/to/your/script.php';\n$params = 'param1 param2'; // Add your parameters here\n\n// Build the command\n$command = "php " . $scriptPath . " " . $params;\n\n// Run the command in the background\nrunInBackground($command);\n\n// Continue executing the main script\necho "Main script continues\n";\n\n// Add any additional logic here