<?php\n// Define encryption and decryption keys\ndefine('ENCRYPTION_KEY', 'your-encryption-key');\n\n// Function to encrypt data\nfunction encryptData($data, $key) {\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted = openssl_encrypt($data, 'aes-256-cbc', $key, 0, $iv);\n    return base64_encode($iv . $encrypted);\n}\n\n// Function to decrypt data\nfunction decryptData($data, $key) {\n    $decodedData = base64_decode($data);\n    $iv = substr($decodedData, 0, openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted = substr($decodedData, openssl_cipher_iv_length('aes-256-cbc'));\n    return openssl_decrypt($encrypted, 'aes-256-cbc', $key, 0, $iv);\n}\n\n// Data to be stored in the cookie\n$data = json_encode([\n    'username' => 'bob',\n    'email' => 'bob@example.com'\n]);\n\n// Encrypt the data\n$encryptedData = encryptData($data, ENCRYPTION_KEY);\n\n// Set the encrypted data in the cookie\nsetcookie('user_data', $encryptedData, time() + (86400 * 30), "/"); // 86400 = 1 day\n\n// Retrieving and decrypting the data from the cookie\nif (isset($_COOKIE['user_data'])) {\n    $decryptedData = decryptData($_COOKIE['user_data'], ENCRYPTION_KEY);\n    $userData = json_decode($decryptedData, true);\n    echo 'Welcome back, ' . htmlspecialchars($userData['username']) . '!';\n}\n?>