<?php\n\nfunction fetchGoogleResults($query, $numPages = 3) {\n    $results = [];\n    $urlTemplate = "https://www.google.com/search?q=" . urlencode($query) . "&start=%d";\n    \n    for ($page = 0; $page < $numPages; $page++) {\n        $start = $page * 10;\n        $url = sprintf($urlTemplate, $start);\n        $html = fetchUrl($url);\n        \n        // Process the HTML to extract the search results (this is a simplified example)\n        preg_match_all('/<div class="BNeawe vvjwJb AP7Wnd">(.*?)<\/div>/', $html, $matches);\n        $results = array_merge($results, $matches[1]);\n    }\n    \n    return $results;\n}\n\nfunction fetchUrl($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/85.0.4183.121 Safari/537.36');\n    $output = curl_exec($ch);\n    curl_close($ch);\n    return $output;\n}\n\n// Example usage:\n$results = fetchGoogleResults('example search query');\nprint_r($results);\n?>