function top_menu_logged_in_sub_menu($user, $page = 'main')\n{\n    $menu_items = [\n        'main' => 'My Admin',\n        'myProjects' => 'My Projects',\n        'insertProject' => 'Post a Project',\n        'myAccount' => 'My Account'\n    ];\n\n    $sub_menu_html = '';\n    foreach ($menu_items as $item_page => $item_name) {\n        $class = ($page == $item_page) ? 'sub_active_link' : 'sub_link';\n        $sub_menu_html .= "\n <li><a href=\"?page={$item_page}\" class=\"{$class}\">{$item_name}</a></li>\n";\n    }\n\n    $sub_menu_html .= "\n <li>Welcome, {$user}! &nbsp;&nbsp;<a href=\"logout.php\">Sign Out</a>&nbsp;&nbsp;</li>\n";\n\n    return $sub_menu_html;\n}