class BlogPost {\n    private $title;\n    private $content;\n    \n    public function __construct($title, $content) {\n        $this->title = $title;\n        $this->content = $content;\n    }\n\n    public function getTitle() {\n        return $this->title;\n    }\n\n    public function getContent() {\n        return $this->content;\n    }\n}\n\n// Usage\n$post = new BlogPost('My First Post', 'This is the content of the first post.');\necho $post->getTitle(); // Outputs: My First Post