<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "bookfeather";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get list of all tables in the database\n$tablesResult = $conn->query("SHOW TABLES");\n$tables = [];\nwhile ($row = $tablesResult->fetch_array()) {\n    $tables[] = $row[0];\n}\n\n// Initialize an empty array to store the votes for each site\n$siteVotes = [];\n\n// Loop through each table and sum the votes_up for each site\nforeach ($tables as $table) {\n    $result = $conn->query("SELECT site, SUM(votes_up) as total_votes_up FROM $table GROUP BY site");\n\n    while ($row = $result->fetch_assoc()) {\n        $site = $row['site'];\n        $votesUp = $row['total_votes_up'];\n\n        if (isset($siteVotes[$site])) {\n            $siteVotes[$site] += $votesUp;\n        } else {\n            $siteVotes[$site] = $votesUp;\n        }\n    }\n}\n\n// Sort the sites by total votes_up in descending order\narsort($siteVotes);\n\n// Get the top 25 sites\n$topSites = array_slice($siteVotes, 0, 25, true);\n\n// Print the top 25 sites\necho "<table border='1'>";\necho "<tr><th>Site</th><th>Total Votes Up</th></tr>";\nforeach ($topSites as $site => $totalVotesUp) {\n    echo "<tr><td>" . htmlspecialchars($site) . "</td><td>" . htmlspecialchars($totalVotesUp) . "</td></tr>";\n}\necho "</table>";\n\n$conn->close();\n?>