// To store\n$serializedArray = base64_encode(json_encode($yourArray));\n\n// To retrieve\n$decodedData = base64_decode($serializedArray);\n$unserializedArray = json_decode($decodedData, true);\n\nif (json_last_error() === JSON_ERROR_NONE) {\n    // Successfully decoded\n    print_r($unserializedArray);\n} else {\n    // JSON decode failed\n    echo "Error: Failed to decode JSON data.";\n}