// Fetch serialized data from the database\n$serializedArray = /* your code to fetch data */;\n\n// Decode the base64 encoded string\n$decodedData = base64_decode($serializedArray);\n\nif ($decodedData !== false) {\n    // Attempt to unserialize the decoded data\n    $unserializedArray = @unserialize($decodedData);\n\n    if ($unserializedArray !== false) {\n        // Successfully unserialized\n        // Process your array here\n        print_r($unserializedArray);\n    } else {\n        // Unserialization failed\n        echo "Error: Failed to unserialize the data.";\n    }\n} else {\n    // Base64 decode failed\n    echo "Error: Failed to decode the base64 string.";\n}