// Example of adding fields to a content type programmatically in a custom module.\n\nfunction mymodule_install() {\n  // Define the 'retro_console' content type.\n  node_type_save((object) array(\n    'type' => 'retro_console',\n    'name' => 'Retro Console',\n    'base' => 'node_content',\n    'description' => 'A content type for cataloguing retro game consoles and computers.',\n    'custom' => TRUE,\n    'modified' => TRUE,\n    'locked' => FALSE,\n  ));\n\n  // Add fields to the 'retro_console' content type.\n  field_create_field(array(\n    'field_name' => 'field_manufacturer',\n    'type' => 'text',\n  ));\n  field_create_instance(array(\n    'field_name' => 'field_manufacturer',\n    'entity_type' => 'node',\n    'bundle' => 'retro_console',\n    'label' => 'Manufacturer',\n    'widget' => array(\n      'type' => 'text_textfield',\n    ),\n  ));\n\n  field_create_field(array(\n    'field_name' => 'field_model',\n    'type' => 'text',\n  ));\n  field_create_instance(array(\n    'field_name' => 'field_model',\n    'entity_type' => 'node',\n    'bundle' => 'retro_console',\n    'label' => 'Model',\n    'widget' => array(\n      'type' => 'text_textfield',\n    ),\n  ));\n\n  field_create_field(array(\n    'field_name' => 'field_serial_number',\n    'type' => 'text',\n  ));\n  field_create_instance(array(\n    'field_name' => 'field_serial_number',\n    'entity_type' => 'node',\n    'bundle' => 'retro_console',\n    'label' => 'Serial Number',\n    'widget' => array(\n      'type' => 'text_textfield',\n    ),\n  ));\n\n  // Add more fields as needed...\n}