<?php\n// Database connection details\n$serverName = "your_server_name";\n$connectionOptions = array(\n    "Database" => "your_database",\n    "Uid" => "your_username",\n    "PWD" => "your_password"\n);\n\n// Connect to SQL Server\n$conn = sqlsrv_connect($serverName, $connectionOptions);\n\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Define the stored procedure and parameters\n$procedure = "{CALL your_stored_procedure(?, ?, ?)}";\n$params = array(\n    array($param1, SQLSRV_PARAM_IN),\n    array($param2, SQLSRV_PARAM_IN),\n    array($returnValue, SQLSRV_PARAM_OUT)\n);\n\n// Execute the stored procedure\n$stmt = sqlsrv_query($conn, $procedure, $params);\n\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Fetch the return value\nsqlsrv_next_result($stmt); // Move to the next result (if any)\n$returnValue = $params[2][0]; // Retrieve the return value\n\necho "The return value is: " . $returnValue;\n\n// Free statement and connection resources\nsqlsrv_free_stmt($stmt);\nsqlsrv_close($conn);\n?>