try {\n    // Test piece straight off the Zend tutorial\n    $slashdotRss = Zend_Feed::import('http://rss.slashdot.org/Slashdot/slashdot');\n} catch (Zend_Feed_Exception $e) {\n    // feed import failed\n    echo "Feed exception caught importing feed: {$e->getMessage()}\n";\n    exit;\n} catch (Zend_Http_Client_Adapter_Exception $e) {\n    // HTTP client adapter exception\n    echo "HTTP client adapter exception caught: {$e->getMessage()}\n";\n    exit;\n} catch (Exception $e) {\n    // catch any other exceptions\n    echo "General exception caught: {$e->getMessage()}\n";\n    exit;\n}