<?php\n// vote.php\nif(isset($_POST['id'])) {\n    $commentId = intval($_POST['id']);\n\n    // Database connection\n    $conn = new mysqli('localhost', 'username', 'password', 'database');\n\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Update vote count in database\n    $sql = "UPDATE comments SET votes = votes + 1 WHERE id = $commentId";\n    if ($conn->query($sql) === TRUE) {\n        // Retrieve the updated vote count\n        $result = $conn->query("SELECT votes FROM comments WHERE id = $commentId");\n        if ($result->num_rows > 0) {\n            $row = $result->fetch_assoc();\n            echo $row['votes'];\n        }\n    } else {\n        echo "Error: " . $sql . "<br>" . $conn->error;\n    }\n\n    $conn->close();\n}\n?>