<?php\n   // Example: Retrieve contacts\n   $contactsFolderId = new DistinguishedFolderIdType();\n   $contactsFolderId->setId('contacts');\n\n   $request = new CreateItemType();\n   $request->setParentFolderId(new FolderIdType($contactsFolderId));\n\n   $response = $api->getContacts();\n   foreach ($response as $contact) {\n       echo 'Name: ' . $contact->getGivenName() . ' ' . $contact->getSurname() . PHP_EOL;\n       echo 'Email: ' . $contact->getEmailAddresses()->getEntry()[0]->getValue() . PHP_EOL;\n   }